dynamicSkillExample.content:
from mcp.server.fastmcp import FastMCP

mcp = FastMCP("DevTools")

@mcp.tool()
def crear_ticket_linear(titulo: str, prioridad: str = "baja") -> str:
    ticket_id = f"LIN-{hash(titulo) % 10000}"
    return f"Ticket {ticket_id} creado con prioridad={prioridad}"

if __name__ == "__main__":
    mcp.run()

